import { Component } from '@angular/core';

@Component({
  selector: 'app-add-product',
  standalone: false,
  templateUrl: './add-product.component.html',
  styleUrl: './add-product.component.css'
})
export class AddProductComponent {
  imagenPreview: string | ArrayBuffer | null = null;

  mostrarVistaPrevia(event: any): void {
    const file = event.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = () => {
        this.imagenPreview = reader.result;
      };
      reader.readAsDataURL(file);
    }
  }

  guardarProducto(event: Event): void {
    event.preventDefault();
    console.log('Producto guardado (simulado)');
  }
}
<div class="add-product-container">
  <h2>🛍️ Añadir nuevo producto</h2>

  <form class="formulario-producto" (submit)="guardarProducto($event)">
    <div class="seccion-form">
      <h3>📦 Información</h3>

      <div class="campo-doble">
        <div>
          <label for="nombre">Nombre</label>
          <input type="text" id="nombre" placeholder="Nombre del producto" />
        </div>

        <div>
          <label for="precio">Precio (€)</label>
          <input type="number" id="precio" placeholder="0.00" />
        </div>
      </div>

      <label for="descripcion">Descripción</label>
      <textarea id="descripcion" rows="3" placeholder="Describe tu producto..."></textarea>

      <label for="categoria">Categoría</label>
      <select id="categoria">
        <option value="">Selecciona una categoría</option>
        <option value="coche">🚗 Coche</option>
        <option value="moto">🏍️ Moto</option>
        <option value="hogar">🏠 Hogar</option>
        <option value="moda">👕 Moda</option>
        <option value="tecnologia">💻 Tecnología</option>
      </select>
    </div>

    <div class="seccion-form">
      <h3>🖼️ Imagen</h3>

      <div class="custom-file-upload">
        <input type="file" id="imagen" (change)="mostrarVistaPrevia($event)" />
        <span>Seleccionar imagen</span>
      </div>

      <div class="preview-img" *ngIf="imagenPreview">
        <img [src]="imagenPreview" alt="Vista previa" />
      </div>
    </div>

    <button type="submit" class="btn-guardar">Guardar</button>
  </form>
</div>
